function generateLoremIpsum(t=5){const e=["lorem","ipsum","dolor","sit","amet","consectetur","adipiscing","elit","sed","do","eiusmod","tempor","incididunt","ut","labore","et","dolore","magna","aliqua","enim","ad","minim","veniam","quis","nostrud","exercitation","ullamco","laboris","nisi","aliquip","ex","ea","commodo","consequat"];let o="";for(let n=0;n<t;n++){let t="";const n=Math.floor(8*Math.random())+8;for(let o=0;o<n;o++){t+=e[Math.floor(Math.random()*e.length)]+" "}t=t.trim(),t=t.charAt(0).toUpperCase()+t.slice(1)+". ",o+=t}return o}const copyLoremIpsum=t=>{const e=generateLoremIpsum(t);clickCopy(e)};timeButton("#section8","Lorem Ipsum","btn btn-secondary btn-lg btn-fix-width btn-block btn-center");